<script>

	function loadImageAsync(url) {
	  return new Promise(function(resolve, reject) {
		const image = new Image();

		image.onload = function() {
		  var base64 = image2Base64(image);
		  resolve(base64);
		};

		image.onerror = function() {
		  reject(new Error('Could not load image at ' + url));
		};

		image.src = url;
	  });
	}


	/**
			将图片转换为Base64 示例
			$("#tijiao").click(function(){		
				//var imgurl = "http://b-ssl.duitang.com/uploads/item/201509/25/20150925110828_iMnGx.jpeg"; //外部图片需要注意跨域问题,要不生成的base64等于空值
				var imgurl = $(".z_addImg img").attr("src");  //获取的图片路径
				var img = new Image();
				img.src=imgurl;
				var base64 = image2Base64(img);
				alert(base64);   //弹出base64图片字符串看看，如果很长很长就成功啦
			})
	 */
	function image2Base64(img) {
		try{
			var canvas = document.createElement("canvas");
			canvas.width = img.width;
			canvas.height = img.height;
			var ctx = canvas.getContext("2d");
			ctx.drawImage(img, 0, 0, img.width, img.height);
			//var dataURL = canvas.toDataURL("image/png");  //转换为 png 格式的 base64  含有 data:image/png;base64,
			var dataURL = canvas.toDataURL("image/jpeg");  //转换为 jpg格式的 base64 ,含有 data:image/jpeg;base64,
			dataURL = dataURL.substring(dataURL.indexOf(",") + 1);  //不含有 data:image/png;base64,  data:image/jpeg;base64,
			return dataURL;
		}catch(e){
			return "";
			//TODO handle the exception
		}
	}
		
</script>
